  type: 'safety',
  original: 'engine protection',
  suggested: 'edge protection',
  confidence: 'HIGH',
  reason: 'Safety term correction',
  businessRisk: 'HIGH',
  requiresReview: true,
  contextBefore: 'Temporary',
  contextAfter: 'needs fixing',
