              </div>

              <div className="mt-3 text-xs text-gray-500">
                ðŸ’¡ AssemblyAI system with construction vocabulary fixes "at 30" â†’ "at 8:30" and "safe farming" â†’ "safe working"
              </div>
            </div>

            {/* Processing Button */}
            {processingResult?.status === 'pending' && (
              <div className="text-center space-y-3">
                <button
                  onClick={handleProcessWithAI}
                  disabled={processingLoading}
                  className="w-full btn-primary disabled:opacity-50 flex items-center justify-center"
                >
                  {processingLoading ? (
